'use strict';

var app = (function () {

    var docElem = document.documentElement,

        _userAgentInit = function () {
            docElem.setAttribute('data-useragent', navigator.userAgent);
        },
        _init = function () {
            _userAgentInit();
        };

    return {
        init: _init
    };

})();

(function () {

    app.init();

    var $bookingCase = $('.booking__case');
    var $bookingMainCase = $('.booking__case-main-container');

    if ($bookingCase.length > 0) {

        function calculateBookingPosition () {
            if ($(window).width() > 992) {


                if ($(window).scrollTop() > parseInt($('.image__container').height())-40) {
                    $bookingMainCase.addClass('show-sub-menu');

                    if ($(window).scrollTop() < $('#map').offset().top - $('.booking__case-main-container').height() - 140) {
                        $bookingMainCase.addClass('fixed');
                        $bookingMainCase.css('top', 100);
                        console.log('a');
                    } else {
                        $bookingMainCase.removeClass('fixed');
                        $bookingMainCase.css('top', $('#map').offset().top - $('.booking__case-main-container').height() - parseInt($('.image__container').height() + 100));
                        console.log('b');
                    }

                } else {
                    $bookingMainCase.removeClass('show-sub-menu');
                    $bookingMainCase.removeClass('fixed');
                    $bookingMainCase.css('top', 0);
                }

            } else {
                $bookingMainCase.removeClass('fixed');
                $bookingMainCase.css('top', 0);
            }
        }

        $(window).on('resize', function (e) {
            calculateBookingPosition();
        });

        $(window).on('scroll', function (e) {
            calculateBookingPosition();
        });
    }

    function amenitiesMore (e) {
        e.preventDefault();
        $(this).parent().parent().find('.hidden-services').slideDown('slow');
        $(this).fadeOut('slow');
    }

    function moreFunc (e) {
        e.preventDefault();
        $(this).parent().find('.hidden-more').slideDown('slow');
        $(this).parent().find('.hidden-content').addClass('shown');
        $(this).fadeOut('slow');
    }

    $('.amenities-more').on('click', amenitiesMore);
    $('.more').on('click', moreFunc);

    function showBookingMobile (e) {
        e.preventDefault();
        $('.booking__case-main-container').slideDown('fast');
    }

    function closeBookingMobile (e) {
        e.preventDefault();
        $('.booking__case-main-container').slideUp('fast');
    }

    if ($(window).width() < 768) {
        $('.booking__submit').on('click', showBookingMobile);
        $('.booking__case-main-container .close').on('click', closeBookingMobile);
    }

})();

$(function(){

    /* Search */
    $("#double_number_range").ionRangeSlider({
        type: "double",
        min: 0,
        max: 400,
        from: 24,
        to: 128,
        step: 1,
        onStart: function (data) {
            console.log(data);
        },
        onChange: function (data) {
            console.log(data);
        },
        onFinish: function (data) {
            console.log(data);
        },
        onUpdate: function (data) {
            console.log(data);
        }
    });

    var nowDate = new Date();
    var today = new Date(nowDate.getFullYear(), nowDate.getMonth(), nowDate.getDate(), 0, 0, 0, 0);

    $('.input-daterange').datepicker({
        autoclose: true,
        todayHighlight: true,
        startDate: today,
        language: 'es',
        orientation: 'auto'
    });

    if ($('body').hasClass('home')) {
        $(window).scroll(function () {
           if ($(window).scrollTop() > $('.home__search').offset().top - 60) {
               $('.header').removeClass('home');
           } else {
               $('.header').addClass('home');
           }
        });
    }

    function showSubSearchDate (e) {
        e.preventDefault();
        $('.home__subsearch').addClass('show-dates');
    }

    function showSubSearchLocation (e) {
        e.preventDefault();
        $('.home__subsearch').removeClass('show-dates');
    }

    /* User Menu */
    function userSubmenuOpen (e) {
        e.preventDefault();
        e.stopPropagation();
        $('.header__user-menu').slideDown('fast');
    }

    function userSubmenuClose () {
        $('.header__user-menu').fadeOut('fast');
    }

    $('#user_submenu').on('click', userSubmenuOpen);

    $('body').on('click', userSubmenuClose);

    $('.home__subsearch-date').on('click', showSubSearchDate);
    $('.home__subsearch-location').on('click', showSubSearchLocation);


    /* Mis Casas */
    function publishHouse(e) {
        e.preventDefault();
        var container = $(this).parent();

        if (container.hasClass('true')) {
            container.removeClass('true');
        } else {
            container.addClass('true');
        }
    }

    $('.my-houses__state-item-btn').on('click', publishHouse);

    function searchMultiple (e) {
        e.preventDefault();

        $(this).toggleClass('multiple');
        $('.search__extra-info').toggleClass('show-multiple');
    }

    $('.js-multiple-search').on('click', searchMultiple);


    function updateSelected () {
        $('#houses-selected').html($('.selected').length);
    }

    function selectMultiple(e) {
        e.preventDefault();
        $(this).parents('.search__house-item').toggleClass('selected');

        updateSelected();
    }

    $('.house__related-select-action').on('click', selectMultiple);

});
